mapper.update:
UPDATE USER_LIST
SET PID=#{pid}
WHERE USER_IDX=#{user_idx}

mapper.select:
SELECT DISTINCT W.RISK_GRADE, W.WORK_IDX, W.WORKLEVEL, W.PIC_NAME, W.WORKTITLE, DATE_FORMAT( W.STARTDATE , '%Y%m%d' ) STARTDATE ,DATE_FORMAT( W.ENDDATE , '%Y%m%d' ) ENDDATE , IFNULL( P.CHECKYN, 'N' ) CHECKYN FROM WORK_LIST W LEFT OUTER JOIN ( SELECT WORK_IDX, WORKDATE, CHECKYN, CHK_USER_IDX FROM PRINT_LIST WHERE WORKDATE = #{searchdate} ) P ON W.WORK_IDX = P.WORK_IDX WHERE W.SITE_IDX = #{siteidx}
AND DATE_FORMAT( W.STARTDATE , '%Y%m%d' ) <= #{searchdate}
AND DATE_FORMAT( W.ENDDATE , '%Y%m%d' ) >= #{searchdate}

mapper.update:
UPDATE PRINT_LIST
SET CHECKYN = #{checkyn} , CHK_USER_IDX = #{user_idx}
WHERE WORK_IDX = #{work_idx}
  AND WORKDATE = #{workdate}
