mapper.select:
   	SELECT *
	FROM PRINT_LIST
	WHERE 
		WORK_IDX = #{work_idx}
		AND WORKDATE = #{workdate}

mapper.insert:
   UPDATE 
     	PRINT_LIST
     SET
		CHK_USER_IDX = #{chk_user_idx}
		,CHECKYN = #{checkyn}
		,PTW = #{ptw}
		,PUI = #{pui}
		,TBM = #{tbm}
		
	WHERE
		WORK_IDX = #{work_idx}
		AND WORKDATE = #{workdate}
