mapper.select:
   	SELECT *
	FROM Tool_LIST
	WHERE WORK_IDX = #{work_idx}
	AND DELYN='N'
	ORDER BY TOOLTYPE;

mapper.update:
     UPDATE 
     	TOOl_LIST
     SET
		TOOLTYPE = #{tooltype}
		,TOOLCODE = #{toolcode}
		,TOOLNAME = #{toolname}
	WHERE
		TOOl_IDX = #{tool_idx}

UPDATE TOOl_LIST
SET DELYN='Y'		
WHERE WORK_IDX = #{work_idx}
