mapper.select:
SELECT *
FROM WORK_LIST_WITH_NAME W  
JOIN SITE_LIST S
ON W.SITE_IDX = S.SITE_IDX
WHERE W.DELYN='N'
AND S.DELYN='N'
AND (
W.WORKTYPE like #{worktype}
OR W.WORKTITLE like #{worktitle}
OR W.USERNAME like #{username}
)
AND W.SITE_IDX like #{site_idx}
ORDER BY W.STARTDATE, W.STARTTIME
LIMIT #{start}, #{size}

        SELECT COUNT(*) 
		FROM WORK_LIST_WITH_NAME W
		JOIN SITE_LIST S
		ON W.SITE_IDX = S.SITE_IDX
		WHERE 
			W.DELYN='N' 
			AND S.DELYN='N' 
			AND (
			W.WORKTYPE like #{worktype}
			OR W.WORKTITLE like #{worktitle}
			OR W.USERNAME like #{username}
			)
			AND W.SITE_IDX like #{site_idx}

mapper.update:
     UPDATE 
     	WORK_LIST
     SET
     	CONT_IDX = #{cont_idx}
		,ISCHARGE = #{ischarge}
		,WORKTYPE = #{worktype}
		,WORKSTATUS = #{workstatus}
		,CATEGORY1 = #{category1}
		,CATEGORY2 = #{category2}
		,CATEGORY3 = #{category3}	
		,WORKCODE = #{workcode}	
		,WORKNAME = #{workname}
		,WORKTITLE = #{worktitle}
			
		,STARTDATE = #{startdate}
		,STARTTIME = #{starttime}
		,ENDDATE = #{enddate}
		,ENDTIME = #{endtime}
		,PLACECODES = #{placecodes}
		,PLACENAMES = #{placenames}
		,ADDR_DETAIL = #{addr_detail}
		
		,PIC_NAME = #{pic_name}
		,PIC_BIRTH = #{pic_birth}
		,PIC_PHONE = #{pic_phone}	
		,PIC_POSITION = #{pic_position}
		,PIC_POS_DETAIL = #{pic_pos_detail}
		,PIC_NUM_WORKER = #{pic_num_worker}
		,WORKLEVEL = #{worklevel}
		,RISK_LEVEL = #{risk_level}
		,RISK_GRADE = #{risk_grade}		
		,RISK_WARN = #{risk_warn}
		,PTW_EXIST = #{ptw_exist}
		,PUI_EXIST = #{pui_exist}
		,REMARK = #{remark}		
		,REMARK_LEADER = #{remark_leader}
		,REMARK_CHIEF = #{remark_chief}
		,INSPEC_MGR_IDX = #{inspec_mgr_idx}
		,UPDATETIME = sysdate()
	WHERE
		WORK_IDX = #{work_idx}

UPDATE WORK_LIST
SET DELYN='Y'
WHERE WORK_IDX = #{work_idx}
