mapper.select:
SELECT *
FROM WORK_LIST_WITH_NAME W  
JOIN SITE_LIST S
ON W.SITE_IDX = S.SITE_IDX
WHERE W.DELYN='N'
AND S.DELYN='N'
AND (
W.WORKTYPE like #{worktype}
OR W.WORKTITLE like #{worktitle}
OR W.USERNAME like #{username}
)
ORDER BY W.WORK_IDX
LIMIT #{start}, #{size}

        SELECT COUNT(*) 
		FROM WORK_LIST_WITH_NAME W
		JOIN SITE_LIST S
		ON W.SITE_IDX = S.SITE_IDX
		WHERE 
			W.DELYN='N' 
			AND S.DELYN='N' 
			AND (
			W.WORKTYPE like #{worktype}
			OR W.WORKTITLE like #{worktitle}
			OR W.USERNAME like #{username}
			)

mapper.update:
     UPDATE 
     	WORK_LIST
     SET
		ISCHARGE = #{ischarge}
		,WORKTYPE = #{workstatus}
		,WORKSTATUS = #{worktype}
		,CATEGORY1 = #{category1}
		,CATEGORY2 = #{category2}
		,CATEGORY3 = #{category3}	
		,WORKCODe = #{workcode}	
		,WORKNAME = #{workname}
		,WORKTITLE = #{worktitle}
			
		,STARTDATE = #{startdate}
		,STARTTIME = #{starttime}
		,ENDDATE = #{enddate}
		,ENDTIME = #{endtime}
		,PLACECODE = #{placecode}
		,PLACENAME = #{placename}
		,ADDR_DETAIL = #{addr_detail}
		,INDOOR = #{indoor}
		
		,PIC_NAME = #{pic_name}
		,PIC_BIRTH = #{pic_birth}
		,PIC_PHONE = #{pic_phone}	
		,PIC_POSITION = #{pic_position}
		,PIC_NUM_WORKER = #{pic_num_worker}
		,WORKLEVEL = #{worklevel}
		,REMARK = #{remark}
		,UPDATETIME = sysdate()
	WHERE
		WORK_IDX = #{work_idx}

UPDATE WORK_LIST
SET DELYN='Y'
WHERE WORK_IDX = #{work_idx}
